function move_up(p)
  p.y = p.y - p.speed
end

function move_down(p)
  p.y = p.y + p.speed
end

function move_left(p)
  p.x = p.x - p.speed
end

function move_right(p)
  p.x = p.x + p.speed
end

players = {
  bolek={name="Bolek";
         x=0, y=0, speed=1;
         controls={
           [58]=move_up,
           [59]=move_down,
           [60]=move_left,
           [61]=move_right
           -- 63 is ctrl, 50 is return
         },
         spr=257},
  lolek={name="Lolek",
         x=32, y=32, speed=1;
         controls={
           [23]=move_up,   -- W
           [19]=move_down, -- S
           [1]=move_left,  -- A
           [4]=move_right  -- D
           -- 49 is tab
         },
         spr=258}
}

level = {
  offset={x=8*6, y=0}
}

function draw_player(p)
  spr(p.spr, p.x + level.offset.x, p.y + level.offset.y, 8)
end

function move_player(p)
  for key_id, fun in pairs(p.controls) do
    if key(key_id) then
      fun(p)
    end
  end
end

function debug(x)
  print(x, 20, 19, 0, false, 1, true)
  print(x, 20, 21, 0, false, 1, true)
  print(x, 19, 20, 0, false, 1, true)
  print(x, 21, 20, 0, false, 1, true)

  print(x, 20, 20, 10, false, 1, true)
end

function TIC()
  move_player(players.bolek)
  move_player(players.lolek)

  cls()
  map(0,0,30,17,0,0)
  rectb(0,0,240,136,3)
  draw_player(players.bolek)
  draw_player(players.lolek)
end

