function stir_up(p)
  p.dx,p.dy = 0,-p.speed
  p.state = "walking_ver"
end

function stir_down(p)
  p.dx,p.dy = 0,p.speed
  p.state = "walking_ver"
end

function stir_left(p)
  p.dx,p.dy = -p.speed,0
  p.state = "walking_hor"
end

function stir_right(p)
  p.dx,p.dy = p.speed,0
  p.state = "walking_hor"
end

